"use strict";const bibleData=require("../data/bible.json");function isValidBook(i){return bibleData.hasOwnProperty(i)}function isValidChapter(i,e){if(!isValidBook(i))return!1;return bibleData[i].hasOwnProperty(e)}function isValidVerse(i,e,a){if(!isValidChapter(i,e))return!1;const r=bibleData[i][e];return a>=1&&a<=r.length}module.exports={isValidBook:isValidBook,isValidChapter:isValidChapter,isValidVerse:isValidVerse};